class Component{constructor(name,content,aliases,style){this.name=name;this.content=content;this.aliases=aliases;this.style=style;this.body=`<div class='${this.name}' ${this.style ? `style="${this.style}"` : ""}>`}
render(){for(let property in this.content){const alias=this.aliases[property];this.type(alias,property);this.body+=`</div>`;this.body=this.body.replace(/:n/g,'<br>');this.body=this.body.replace('undefined','');return this.body}}
type(alias,property){switch(alias){case 'link':this.body+=`<a href="${this.content[property].href}" ${alias.class ? `class="${alias.class}"` : ""} ${alias.id ? `id="${alias.id}"` : ""} style="${alias.style}">${this.content[property].content}</a>`;break;case 'img':this.body+=`<img src="${this.content[property]}" ${alias.class ? `class="${alias.class}"` : ""} ${alias.id ? `id="${alias.id}"` : ""} style="${alias.style}"/>`;break;default:this.body+=`<${alias.type} ${alias.class ? `class="${alias.class}"` : ""} ${alias.id ? `id="${alias.id}"` : ""} style="${alias.style}">${this.content[property]}</${alias.type}>`;break}}}
class Group{constructor(name,classname,id,style){this.name=name;this.classname=classname;this.id=id;this.style=style}
include(components){let content;for(let component of components){content+=component.render()}
content=content.replace('undefined','');return `<div class="${this.classname ? this.classname : ''}" id="${this.id ? this.id : ''}" style="${this.style ? this.style : ''}">${content}</div>`}}